import React from 'react';
import { format } from 'date-fns';

const Message = ({ message, isOwnMessage, onReply }) => {
  const formatTime = (timestamp) => {
    return format(new Date(timestamp), 'HH:mm');
  };

  const handleReply = () => {
    onReply(message);
  };

  return (
    <div
      className={`flex mb-2 sm:mb-3 md:mb-4 message-animate ${
        isOwnMessage ? 'justify-end' : 'justify-start'
      }`}
    >
      <div
        className={`max-w-[75%] sm:max-w-[70%] md:max-w-[65%] px-2.5 sm:px-3 md:px-4 py-1.5 sm:py-2 md:py-3 rounded-lg shadow-sm ${
          isOwnMessage
            ? 'bg-user-message text-gray-800 rounded-br-none'
            : 'bg-message-bg text-gray-800 rounded-bl-none border border-gray-200'
        }`}
      >
        {!isOwnMessage && (
          <div className="text-xs md:text-sm font-semibold text-primary mb-0.5 sm:mb-1">
            {message.username}
          </div>
        )}
        
        {/* Reply to message display */}
        {message.replyTo && (
          <div className="bg-gray-100 border-l-2 border-primary p-2 mb-2 rounded text-xs">
            <div className="font-semibold text-primary">{message.replyTo.username}</div>
            <div className="text-gray-600 truncate">{message.replyTo.text}</div>
          </div>
        )}
        
        <div className="text-xs sm:text-sm md:text-base mb-0.5 sm:mb-1 break-words">{message.text}</div>
        
        <div className="flex justify-between items-center mt-0.5 sm:mt-1">
          <button
            onClick={handleReply}
            className="text-[10px] sm:text-xs text-gray-400 hover:text-primary transition-colors"
          >
            Reply
          </button>
          <div className="text-[10px] sm:text-xs text-gray-500">
            {formatTime(message.timestamp)}
          </div>
        </div>
      </div>
    </div>
  );
};

export default Message;
