import React, { useState, useEffect } from 'react';

const MessageInput = ({ onSendMessage, replyTo, onCancelReply }) => {
  const [message, setMessage] = useState('');

  const handleSubmit = (e) => {
    e.preventDefault();
    if (message.trim()) {
      onSendMessage(message.trim(), replyTo);
      setMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSubmit(e);
    }
    if (e.key === 'Escape' && replyTo) {
      onCancelReply();
    }
  };

  // Focus input when replying
  useEffect(() => {
    if (replyTo) {
      const input = document.querySelector('input[type="text"]');
      input?.focus();
    }
  }, [replyTo]);

  return (
    <div className="border-t border-gray-200 bg-white">
      {/* Reply preview */}
      {replyTo && (
        <div className="px-3 sm:px-4 md:px-6 pt-3 sm:pt-4 md:pt-6 pb-2">
          <div className="max-w-screen-lg mx-auto">
            <div className="bg-gray-100 border-l-2 border-primary p-2 rounded flex justify-between items-start">
              <div className="flex-1 min-w-0">
                <div className="text-xs font-semibold text-primary">Replying to {replyTo.username}</div>
                <div className="text-xs text-gray-600 truncate">{replyTo.text}</div>
              </div>
              <button
                onClick={onCancelReply}
                className="ml-2 text-gray-400 hover:text-gray-600 text-sm"
              >
                âœ•
              </button>
            </div>
          </div>
        </div>
      )}
      
      <div className="p-3 sm:p-4 md:p-6">
        <form onSubmit={handleSubmit} className="flex space-x-2 max-w-screen-lg mx-auto">
          <input
            type="text"
            value={message}
            onChange={(e) => setMessage(e.target.value)}
            onKeyPress={handleKeyPress}
            placeholder={replyTo ? `Reply to ${replyTo.username}...` : "Type a message..."}
            className="flex-1 border border-gray-300 rounded-full px-3 sm:px-4 py-2 sm:py-2 md:py-3 text-sm md:text-base focus:outline-none focus:ring-2 focus:ring-primary focus:border-transparent min-w-0"
          />
          <button
            type="submit"
            disabled={!message.trim()}
            className="bg-primary text-white rounded-full px-4 sm:px-6 md:px-8 py-2 sm:py-2 md:py-3 text-xs sm:text-sm md:text-base font-medium hover:bg-green-600 focus:outline-none focus:ring-2 focus:ring-primary focus:ring-offset-2 disabled:opacity-50 disabled:cursor-not-allowed transition-colors flex-shrink-0"
          >
            {replyTo ? 'Reply' : 'Send'}
          </button>
        </form>
      </div>
    </div>
  );
};

export default MessageInput;
