import React from 'react';
import { format } from 'date-fns';

const Message = ({ message, isOwnMessage }) => {
  const formatTime = (timestamp) => {
    return format(new Date(timestamp), 'HH:mm');
  };

  return (
    <div
      className={`flex mb-3 md:mb-4 message-animate ${
        isOwnMessage ? 'justify-end' : 'justify-start'
      }`}
    >
      <div
        className={`max-w-xs sm:max-w-sm md:max-w-md lg:max-w-lg xl:max-w-xl px-3 py-2 md:px-4 md:py-3 rounded-lg shadow-sm ${
          isOwnMessage
            ? 'bg-user-message text-gray-800 rounded-br-none'
            : 'bg-message-bg text-gray-800 rounded-bl-none border border-gray-200'
        }`}
      >
        {!isOwnMessage && (
          <div className="text-xs md:text-sm font-semibold text-primary mb-1">
            {message.username}
          </div>
        )}
        <div className="text-sm md:text-base mb-1 break-words">{message.text}</div>
        <div className="text-xs text-gray-500 text-right mt-1">
          {formatTime(message.timestamp)}
        </div>
      </div>
    </div>
  );
};

export default Message;
