import React, { useState, useEffect, useCallback } from 'react';
import io from 'socket.io-client';
import MessageList from './MessageList';
import MessageInput from './MessageInput';

const SOCKET_SERVER_URL = 'http://localhost:3001'; // Default WebSocket server URL
const USERNAME = 'User_' + Math.floor(Math.random() * 1000); // Generate random username

const ChatContainer = () => {
  const [socket, setSocket] = useState(null);
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Welcome to the React Chat App! ðŸŽ‰",
      username: "System",
      timestamp: new Date(Date.now() - 300000).toISOString()
    },
    {
      id: 2,
      text: "This is a sample message from another user to demonstrate the chat interface.",
      username: "Alice",
      timestamp: new Date(Date.now() - 240000).toISOString()
    },
    {
      id: 3,
      text: "Your messages will appear on the right side, while others appear on the left.",
      username: "Bob",
      timestamp: new Date(Date.now() - 180000).toISOString()
    },
    {
      id: 4,
      text: "The interface is fully responsive and works great on mobile devices too! ðŸ“±",
      username: USERNAME,
      timestamp: new Date(Date.now() - 120000).toISOString()
    }
  ]);

  // Initialize socket connection
  useEffect(() => {
    const newSocket = io(SOCKET_SERVER_URL);
    setSocket(newSocket);

    // Socket event listeners
    newSocket.on('connect', () => {
      console.log('Connected to WebSocket server');
    });

    newSocket.on('message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    // Cleanup on unmount
    return () => {
      newSocket.close();
    };
  }, []);

  // Send message handler
  const sendMessage = useCallback((text) => {
    if (socket) {
      const messageData = {
        text,
        username: USERNAME,
        timestamp: new Date().toISOString(),
      };
      
      socket.emit('message', messageData);
      
      // Optimistically add message to UI
      setMessages((prevMessages) => [...prevMessages, messageData]);
    }
  }, [socket]);

  return (
<div className="flex flex-col h-screen bg-chat-bg max-w-6xl mx-auto shadow-xl">
      <div className="bg-primary text-white p-4 shadow-md">
        <div className="max-w-screen-lg mx-auto">
          <h1 className="text-2xl md:text-3xl font-semibold">React Chat App</h1>
          <p className="text-sm md:text-base opacity-75">Connected as: {USERNAME}</p>
        </div>
      </div>
      
      <MessageList 
        messages={messages}
        currentUser={USERNAME}
      />
      
      <MessageInput 
        onSendMessage={sendMessage}
      />
    </div>
  );
};

export default ChatContainer;
