import React, { useState, useEffect, useCallback } from 'react';
import io from 'socket.io-client';
import MessageList from './MessageList';
import MessageInput from './MessageInput';
import { useAuth } from '../contexts/AuthContext';

const SOCKET_SERVER_URL = 'http://localhost:3001'; // Default WebSocket server URL

const ChatContainer = () => {
  const { user, logout } = useAuth();
  const [socket, setSocket] = useState(null);
  const [replyTo, setReplyTo] = useState(null);
  const [messages, setMessages] = useState([
    {
      id: 1,
      text: "Welcome to the React Chat App! ðŸŽ‰",
      username: "System",
      timestamp: new Date(Date.now() - 300000).toISOString()
    },
    {
      id: 2,
      text: "This is a sample message from another user to demonstrate the chat interface.",
      username: "Alice",
      timestamp: new Date(Date.now() - 240000).toISOString()
    },
    {
      id: 3,
      text: "Your messages will appear on the right side, while others appear on the left.",
      username: "Bob",
      timestamp: new Date(Date.now() - 180000).toISOString()
    },
    {
      id: 4,
      text: "The interface is fully responsive and works great on mobile devices too! ðŸ“±",
      username: user.username,
      timestamp: new Date(Date.now() - 120000).toISOString()
    }
  ]);

  // Initialize socket connection
  useEffect(() => {
    const newSocket = io(SOCKET_SERVER_URL);
    setSocket(newSocket);

    // Socket event listeners
    newSocket.on('connect', () => {
      console.log('Connected to WebSocket server');
    });

    newSocket.on('message', (message) => {
      setMessages((prevMessages) => [...prevMessages, message]);
    });

    // Cleanup on unmount
    return () => {
      newSocket.close();
    };
  }, []);

  // Send message handler
  const sendMessage = useCallback((text, replyToMessage = null) => {
    if (socket) {
      const messageData = {
        id: Date.now() + Math.random(), // Generate unique ID
        text,
        username: user.username,
        timestamp: new Date().toISOString(),
        replyTo: replyToMessage ? {
          id: replyToMessage.id,
          username: replyToMessage.username,
          text: replyToMessage.text
        } : null
      };
      
      socket.emit('message', messageData);
      
      // Optimistically add message to UI
      setMessages((prevMessages) => [...prevMessages, messageData]);
      
      // Clear reply state
      setReplyTo(null);
    }
  }, [socket, user.username]);

  // Handle reply to message
  const handleReply = useCallback((message) => {
    setReplyTo(message);
  }, []);

  // Cancel reply
  const handleCancelReply = useCallback(() => {
    setReplyTo(null);
  }, []);

  return (
    <div className="flex flex-col h-screen bg-chat-bg w-full max-w-6xl mx-auto shadow-xl">
      <div className="bg-primary text-white p-3 sm:p-4 shadow-md">
        <div className="max-w-screen-lg mx-auto">
          <div className="flex justify-between items-center mb-1">
            <h1 className="text-xl sm:text-2xl md:text-3xl font-semibold truncate">React Chat App</h1>
            <button 
              onClick={logout}
              className="px-3 py-1 sm:px-4 sm:py-2 text-xs sm:text-sm bg-red-500 hover:bg-red-600 rounded-lg transition-colors flex-shrink-0 ml-2"
            >
              Logout
            </button>
          </div>
          <p className="text-xs sm:text-sm md:text-base opacity-75 truncate">Connected as: {user.username}</p>
        </div>
      </div>
      
      <MessageList 
        messages={messages}
        currentUser={user.username}
        onReply={handleReply}
      />
      
      <MessageInput 
        onSendMessage={sendMessage}
        replyTo={replyTo}
        onCancelReply={handleCancelReply}
      />
    </div>
  );
};

export default ChatContainer;
